import React, {useState, useEffect} from "react";
import {MapContainer, TileLayer, Marker, Popup} from "react-leaflet";
import L from "leaflet";
import  carrot from '../assets/carrot.png';
import KgvItem from "./KgvItem";
import useLocalStorage from '../useLocalStorage';
import {CopyToClipboard} from 'react-copy-to-clipboard';
import Favourites from "./Favourites";
import {Container, Row, Col} from "react-bootstrap";



function Map() {

    const [kgvs, setKgvs] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(false);
    const [favourites, setFavourites] = useLocalStorage('favourite', []);

    const loadKgvs = (searchQuery) => {
      const API_URL = "https://goschreber.herokuapp.com"
      const url = `${API_URL}/kgvs`
      setIsLoading(true);
      setError(false);

      fetch(url)
        .then((res) => {

        console.log(res);

          if (!res.ok) throw new Error("Oh noo");
          setIsLoading(false);
          return res.json();
        })
        .then((json) => setKgvs(json.data))
        .catch((err) => setError(true));

    };

      useEffect(loadKgvs, []);



      function favouriteGarden(gardenid) {

        console.log(gardenid)
        console.log(favourites.indexOf(gardenid))

        if (favourites.indexOf(gardenid) === -1){
          setFavourites([
            ...favourites,
            gardenid
          ])
        } else {

          setFavourites(favourites.filter(item => item != gardenid))
        }



      }
    //console.log(favourites)

    const position = [51.330743159430824, 12.36348580378971]
    const carrotIcon = L.icon({
      iconUrl: carrot,
      iconSize:     [38, 38], // size of the icon
      shadowSize:   [50, 64], // size of the shadow
      iconAnchor:   [19, 38],
      shadowAnchor: [4, 62],  // the same for the shadow
      popupAnchor:  [0, -38]
    });

    const favouritedItems = kgvs.filter(({_id}) => favourites.indexOf(_id) != -1)
    console.log(favouritedItems)


    return (
    <Container fluid>
      <Row>
        <Col>
              <MapContainer center={position} zoom={13} scrollWheelZoom={false}>
              <TileLayer
              attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              />

            {kgvs.map(verein => (
                <Marker
                  key={verein._id}
                  icon = {carrotIcon}
                  position={[
                    verein.geometry.coordinates[0],
                    verein.geometry.coordinates[1]
                  ]}
                >
                  <Popup>
                  <div>
                    <h2>{verein.kgvname}</h2>
                    <p>{verein.email}</p>
                  </div>
                  </Popup>
                </Marker>  
              ))}
            </MapContainer>
        </Col>

        <Col>
            {kgvs.map(verein => <KgvItem favClick={() => favouriteGarden(verein._id)} verein={verein} key={verein._id}/>)} 
        </Col>
        <Col>
          <h2>Favourite List</h2>
          <CopyToClipboard text={favouritedItems.map(el => el.email)}>
            <button>clipboard all email addresses</button>
          </CopyToClipboard>
          {favouritedItems.map(verein => <Favourites favClick={() => favouriteGarden(verein._id)} verein={verein} key={verein._id}/>)}
        </Col>
      </Row>
    </Container>

    )
}

export default Map
